pragma solidity ^0.5.0;


contract Todolist {
    address public owner;
    uint256 public todos_lenght;

    constructor() public {
        owner = msg.sender;
        todos_lenght = 0;
    }
    
    event NewTodoAdded (
        uint date,
        address from,
        string id,
        string data
    );

    struct todo {
        bool has_data;
        string todo_data;
        bool isfinished;
    }

    mapping(string => todo) todos;
    
    
    function readtodo(string memory _todoid) public view returns(string memory, bool){
        return (todos[_todoid].todo_data, todos[_todoid].isfinished); 
    }

  

    function addtodo(string calldata _todoid, string calldata _data) external {
        require((msg.sender == owner) && (!todos[_todoid].has_data));
        emit NewTodoAdded(now, msg.sender, _todoid, _data);
        todos_lenght++;
        todos[_todoid].todo_data = _data;
        todos[_todoid].has_data = true;
        todos[_todoid].isfinished = false;
        //todos[todos_lenght] = todo(_data, false, todos_lenght); i need to know hwo to add data to struct in a single line <= i tried this code and failed
    }

    function updatetodo(string calldata _todoid, string calldata _data)
        external
    {
        require(msg.sender == owner && todos[_todoid].has_data);
        todos[_todoid].todo_data = _data;
    }
}
